import { ButtonGroup } from "@/components/ui/button-group";

import { Pause, Play, SkipBack, SkipForward, Volume2 } from "lucide-react";

import { useState } from "react";

const MediaButtonGroup = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(75);

  const togglePlay = () => setIsPlaying(!isPlaying);
  const skipBack = () => { /* Skip back 10s */ };
  const skipForward = () => { /* Skip forward 10s */ };
  const toggleMute = () => setVolume(volume > 0 ? 0 : 75);

  return (
    <ButtonGroup>
      <Button variant="outline" size="icon" title="Previous" onClick={skipBack}>
        <SkipBack />
      </Button>
      <Button
        variant={isPlaying ? "default" : "outline"}
        size="icon"
        onClick={togglePlay}
        title={isPlaying ? "Pause" : "Play"}
      >
        {isPlaying ? <Pause /> : <Play />}
      </Button>
      <Button variant="outline" size="icon" title="Next" onClick={skipForward}>
        <SkipForward />
      </Button>
      <Button variant="outline" size="icon" onClick={toggleMute}>
        <Volume2 className={volume === 0 ? "opacity-50" : "opacity-100"} />
      </Button>
    </ButtonGroup>
  );
};

export { MediaButtonGroup }

"use client";

import { Button } from "@/components/ui/button";
import { ButtonGroup } from "@/components/ui/button-group";

import { Bell, BellOff, Clock, Trash } from "lucide-react";

import { useState } from "react";

const NotificationButtonGroup = () => {
  const [notificationsEnabled, setNotificationsEnabled] = useState(true);
  const [notificationCount, setNotificationCount] = useState(5);
  const [isSnoozed, setIsSnoozed] = useState(false);

  const toggleNotifications = () => {
    setNotificationsEnabled(!notificationsEnabled);
    if (notificationsEnabled) {
      setIsSnoozed(false);
    }
  };

  const snoozeNotifications = () => {
    setIsSnoozed(true);
    setNotificationsEnabled(false);
  };

  const clearNotifications = () => {
    setNotificationCount(0);
  };

  return (
    <div className="flex flex-col items-center justify-center w-full gap-3">
      <ButtonGroup>
        <Button
          variant={notificationsEnabled ? "default" : "outline"}
          size="sm"
          onClick={toggleNotifications}
        >
          {notificationsEnabled ? (
            <Bell className="h-4 w-4" />
          ) : (
            <BellOff className="h-4 w-4" />
          )}
          {notificationsEnabled ? "On" : "Off"}
        </Button>
        <Button
          variant={isSnoozed ? "default" : "outline"}
          size="sm"
          onClick={snoozeNotifications}
          disabled={!notificationsEnabled && !isSnoozed}
        >
          <Clock className="h-4 w-4" /> Snooze
        </Button>
        <Button
          variant="outline"
          size="sm"
          onClick={clearNotifications}
          disabled={notificationCount === 0}
        >
          <Trash className="h-4 w-4" /> Clear ({notificationCount})
        </Button>
      </ButtonGroup>

      <p className="text-xs text-slate-400">
        Status:{" "}
        <span className="text-slate-200 font-medium">
          {isSnoozed
            ? "Snoozed"
            : notificationsEnabled
              ? `${notificationCount} notification${notificationCount !== 1 ? "s" : ""}`
              : "Disabled"}
        </span>
      </p>
    </div>
  );
};

export { NotificationButtonGroup }

"use client";

import { Button } from "@/components/ui/button";
import { ButtonGroup, ButtonGroupText } from "@/components/ui/button-group";
import {
  ChevronLeft,
  ChevronRight,
  ChevronsLeft,
  ChevronsRight,
} from "lucide-react";
import { useState } from "react";

const PaginationButtonGroup = () => {
  const [currentPage, setCurrentPage] = useState(5);
  const totalPages = 20;

  const goToFirst = () => setCurrentPage(1);
  const goToPrevious = () => setCurrentPage(Math.max(1, currentPage - 1));
  const goToNext = () => setCurrentPage(Math.min(totalPages, currentPage + 1));
  const goToLast = () => setCurrentPage(totalPages);

  return (
    <ButtonGroup>
      <Button
        variant="outline"
        size="icon"
        onClick={goToFirst}
        disabled={currentPage === 1}
      >
        <ChevronsLeft />
      </Button>
      <Button
        variant="outline"
        size="icon"
        onClick={goToPrevious}
        disabled={currentPage === 1}
      >
        <ChevronLeft />
      </Button>
      <ButtonGroupText>
        {currentPage} / {totalPages}
      </ButtonGroupText>
      <Button
        variant="outline"
        size="icon"
        onClick={goToNext}
        disabled={currentPage === totalPages}
      >
        <ChevronRight />
      </Button>
      <Button
        variant="outline"
        size="icon"
        onClick={goToLast}
        disabled={currentPage === totalPages}
      >
        <ChevronsRight />
      </Button>
    </ButtonGroup>
  );
};

export { PaginationButtonGroup }

