    setGoal(Math.max(200, Math.min(400, goal + adjustment)));
  };

  return (
    <Drawer open={open} onOpenChange={setOpen}>
      <DrawerTrigger asChild>
        <Button className="gap-2">
          <ChevronUp className="h-4 w-4" />
