const getStatusVariant = (status: string) => {
  switch (status) {
    case "delivered":
      return "default";
    case "processing":
      return "secondary";
    case "pending":
      return "outline";
    case "cancelled":
      return "destructive";
    default:
      return "default";
  }
};

